import{_ as a,W as n,X as s,Y as o,Z as e,a1 as t,$ as l}from"./framework-f5e21fe7.js";const c={},i=e("p",null,[t("本文内容主要为图床的基本知识和如何搭建一个"),e("strong",null,"免费"),t("的图床。")],-1),u=l(`<h1 id="_1-图床" tabindex="-1"><a class="header-anchor" href="#_1-图床" aria-hidden="true">#</a> 1. 图床</h1><blockquote><p>什么是图床？</p></blockquote><ul><li>图床是一个用来<u>存储图片的网络服务</u>。它可以让用户上传图片到互联网上，然后通过链接或者嵌入代码将图片展示在其他网站上。</li></ul><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token comment">&lt;!-- 比如像这样 --&gt;</span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>www.xxx.com/xxx.png<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>
<span class="token comment">&lt;!-- 在网站上您就可以看到图片了 --&gt;</span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><blockquote><p>图床优点</p></blockquote><ul><li><ol><li>节省空间：图片存储在云端，在使用图床可以节省本地存储空间</li></ol></li><li><ol start="2"><li>方便分享：使用图床可以方便地将图片分享给他人，只需提供图片链接即可</li></ol></li><li><ol start="3"><li>提高网站速度：使用图床可以提高网站的加载速度，不占用本地服务器的带宽</li></ol></li></ul><h1 id="_2-使用-github-和-picgo-搭建图床" tabindex="-1"><a class="header-anchor" href="#_2-使用-github-和-picgo-搭建图床" aria-hidden="true">#</a> 2. 使用 GitHub 和 PicGo 搭建图床</h1><blockquote><p><code>GitHub</code> 步骤</p></blockquote><ul><li><ol><li>新建仓库（记得此前创建账号）</li></ol></li><li><ol start="2"><li>设置仓库开源 <code>public</code> 可见（保证图片资源能够被访问）</li></ol></li><li><ol start="3"><li>生成私人令牌 <code>token</code>（用于连接 <code>PicGo</code> 图床软件）</li></ol><ul><li>其中权限类型可以只选择 <code>repo</code> 即可</li></ul></li></ul><hr><blockquote><p><code>PicGo</code> 步骤</p></blockquote><ul><li><p>配置 <code>github</code> 设置（依次填写仓库名、分支名为 <code>main</code>（不是 <code>master</code>）、设定 <code>token</code>）</p></li><li><p>现在测试一下！</p></li></ul><div class="language-html line-numbers-mode" data-ext="html"><pre class="language-html"><code><span class="token comment">&lt;!-- 这是我上传后生成的 url 地址 --&gt;</span>
url = &quot;https://raw.githubusercontent.com/tcSteamedEggs/my-blog-pic/main/avatar.jpg&quot;
<span class="token comment">&lt;!-- 因为我使用的博客主题 vuepress-theme-hope 有内置的 markdown 拓展 所以像我这么写后，下面会展示这样的图片 --&gt;</span>
<span class="token tag"><span class="token tag"><span class="token punctuation">&lt;</span>img</span> <span class="token attr-name">src</span><span class="token attr-value"><span class="token punctuation attr-equals">=</span><span class="token punctuation">&quot;</span>https://raw.githubusercontent.com/tcSteamedEggs/my-blog-pic/main/avatar.jpg<span class="token punctuation">&quot;</span></span> <span class="token punctuation">/&gt;</span></span>
</code></pre><div class="line-numbers" aria-hidden="true"><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div><div class="line-number"></div></div></div><img src="https://raw.githubusercontent.com/tcSteamedEggs/my-blog-pic/main/avatar.jpg"><ul><li>甚至我可以修改图片的样式 ~</li></ul><p><img src="https://raw.githubusercontent.com/tcSteamedEggs/my-blog-pic/main/avatar.jpg" style="width:50%;height:50%;display:block;margin:0 auto;border-radius:50%;"></p><blockquote><p>最后，我需要提醒一下，<code>github + PicGo</code> 可能不太好用，在国内访问较慢，图片可能加载不出来（可能你现在就看不到上面的图片了...），甚至可能给挂了 ！</p></blockquote><ul><li>我建议使用<strong>腾讯云 + <code>PicGo</code></strong> 搭建图床，且防止别有用心的人来 <code>DDoS</code> 攻击（将会面临极其高昂的流量费用），同时设置防盗链和 <code>COS</code> 授权与身份认证来尽量避免；还可以搭配 <code>CDN</code> 使用，利用 <code>CDN</code> 的限流限频能力来尽量避免 !</li></ul><hr><div style="text-align:center;"><p><strong>你来试试吧</strong> ~</p></div>`,20);function p(d,r){return n(),s("div",null,[i,o(" more "),u])}const g=a(c,[["render",p],["__file","img_PicGo.html.vue"]]);export{g as default};
